    void insert_data()
    {   
        Object selectedItem = comboClasses.getSelectedItem();
        String classsection = selectedItem.toString();
        long Aadhar_no=Long.parseLong(txtAadhar.getText());
        String Student_name=txtName.getText();
        String Father_name=txtFather.getText();
        String Mother_name=txtMother.getText();
        long Phone_no=Long.parseLong(txtPhone.getText());
        
        String Address=txtAddress.getText();
        String Bank_name=txtBank.getText();
        long Account_no=Long.parseLong(txtAccount.getText());
        
        int counter=0;
        
        try
        {
            Class.forName("com.mysql.jdbc.Driver");
            Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/Transaction_Mgmt","root","");
            Statement stmt=conn.createStatement();
            ResultSet rs=stmt.executeQuery("select * from student_details_abcss");
            while(rs.next())
            {
                if(Aadhar_no==rs.getLong(1))
                {
                    counter++;
                }
            }
            if(counter>0)
            {
                JOptionPane.showMessageDialog(this,"Student associated with the Aadhar number already exists in the database!");
                txtAadhar.setText("");
                counter=0;
            }
            else
            {
                insert();
            }
  
        }
        catch(Exception ex)
        {
            ex.printStackTrace();
        }
    }
    void insert()
    {
        Object selectedItem = comboClasses.getSelectedItem();
        String classsection = selectedItem.toString();
        long Aadhar_no=Long.parseLong(txtAadhar.getText());
        String Student_name=txtName.getText();
        String Father_name=txtFather.getText();
        String Mother_name=txtMother.getText();
        long Phone_no=Long.parseLong(txtPhone.getText());
        
        String Address=txtAddress.getText();
        String Bank_name=txtBank.getText();
        long Account_no=Long.parseLong(txtAccount.getText());
        try
        {
            Class.forName("com.mysql.jdbc.Driver");
            Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/Transaction_Mgmt","root","");
            Statement stmt=conn.createStatement();
            ResultSet rs=stmt.executeQuery("select * from student_details_abcss");
            if(rs.next())
            {
                PreparedStatement pst=conn.prepareStatement("insert into student_details_abcss(Aadhar_no, Student_name, Father_name, Mother_name, Address, Phone_no, Bank_name, Account_no, Class_section) values(?,?,?,?,?,?,?,?,?)");
                pst.setLong(1,Aadhar_no);
                pst.setString(2,Student_name);
                pst.setString(3,Father_name);
                pst.setString(4,Mother_name);
                pst.setString(5,Address);
                pst.setLong(6,Phone_no);
                pst.setString(7,Bank_name);
                pst.setLong(8,Account_no);
                pst.setString(9, classsection);
                pst.executeUpdate();
                JOptionPane.showMessageDialog(this,"New student record added successfully");
            }
        }
        catch(Exception ex)
        {
            ex.printStackTrace();
        }
    }
    void delete_data()
    {
        try
        {
                String aadhar=txtAadhar.getText();
                
                Class.forName("com.mysql.jdbc.Driver");
                Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/Transaction_Mgmt","root","");
                Statement stmt=conn.createStatement();
                ResultSet rs=stmt.executeQuery("select * from student_details_abcss where Aadhar_no='"+aadhar+"'");
                if(rs.next())
                {
                   //int aid=Integer.parseInt(txtAadhar.getText());
                   PreparedStatement pst=conn.prepareStatement("delete from student_details_abcss where Aadhar_no=?");
                   pst.setString(1,aadhar);
                   pst.executeUpdate();
                   JOptionPane.showMessageDialog(this,"Record has been deleted successfully");
                   cleartext();
                   conn.close();  
                }
                else
                {
                   JOptionPane.showMessageDialog(this, "Enter a valid Aadhar Number");
                }
                 
           }
        catch(Exception ex){ex.printStackTrace();}
    }
    public void fillCombo()
    {
        try
        {
            String querycombo="select class_section from class_details_abcss";
            Class.forName("com.mysql.jdbc.Driver");
            Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/transaction_mgmt", "root", "");
            PreparedStatement pst=conn.prepareStatement(querycombo);
            ResultSet rs=pst.executeQuery();
            while(rs.next())
            {
                String classsection=rs.getString("class_section");
                comboClasses.addItem(classsection);
            }
            
            
        }
        catch(Exception e)
        {
            e.printStackTrace();
        }
    }
    void cleartext()
    {
        comboClasses.setModel(new javax.swing.DefaultComboBoxModel<>(new String[] { "--Class--" }));
        fillCombo();
        txtAadhar.setText("");
        txtName.setText("");
        txtFather.setText("");
        txtMother.setText("");
        txtPhone.setText("");
        txtAddress.setText("");
        txtBank.setText("");
        txtAccount.setText("");
    }
