    private void btnSave1ActionPerformed(java.awt.event.ActionEvent evt) { 
        Object selectedItem = comboClasses.getSelectedItem();
        String classsection = selectedItem.toString();
        String aadhar_no=txtAadhar.getText();
        String student=txtName.getText();
        String father=txtFather.getText();
        String mother=txtMother.getText();
        String phoneno=txtPhone.getText();
        String address=txtAddress.getText();
        String bankname=txtBank.getText();
        String account=txtAccount.getText();
        
        if ((aadhar_no.trim().equals("") || student.trim().equals(""))||father.trim().equals("")||mother.trim().equals("")||phoneno.trim().equals("")||address.trim().equals("")||bankname.trim().equals("")||account.trim().equals("")||classsection.trim().equals("---class---"))
        {
            JOptionPane.showMessageDialog(this, "Enter complete student details");
        }
        else
        {
            insert_data();
        } 
        
    }                                        
